custom-workflow.before-save:
@signal_label = "[This label will be removed during the next submitting. Do not delete or modify it manually!]\r"
if @issue.new_record? && !@issue.parent_issue_id.nil? parent_issue_project = Issue.find_by_id(@issue.parent_issue_id).project if @issue.project == parent_issue_project if !@issue.description.include?(@signal_label) need_warning = true @issue.description = @signal_label + @issue.description else need_warning = false @issue.description.sub!(@signal_label, '') end elsif @issue.description.include?(@signal_label) need_warning = false @issue.description.sub!(@signal_label, '') end end
if need_warning raise WorkflowError, "Warning: your new subtask is in the same project as the parent task. If you are sure that all is OK just submit the issue again." end
